CREATE DATABASE cua_hang_sach;
USE cua_hang_sach;

-- Table creation (as provided by the user, with minor corrections)
CREATE TABLE DanhMuc (
    DanhMucID INT PRIMARY KEY,
    TenDanhMuc VARCHAR(255) NOT NULL
);

CREATE TABLE TacGia (
    IDTacGia INT PRIMARY KEY,
    TenTacGia VARCHAR(255) NOT NULL
);

CREATE TABLE NXB (
    ID_NXB INT PRIMARY KEY,
    TenNXB VARCHAR(255) NOT NULL
);

CREATE TABLE NCC (
    ID_NCC INT PRIMARY KEY,
    TenNCC VARCHAR(255) NOT NULL,
    SDT VARCHAR(15),
    Email VARCHAR(255),
    QuanHuyen VARCHAR(255),
    SoNhaDuong VARCHAR(255),
    TinhThanhPho VARCHAR(255)
);

CREATE TABLE SanPham (
    SanPhamID INT PRIMARY KEY,
    TenSanPham VARCHAR(255) NOT NULL,
    ID_NXB INT,
    MoTa TEXT,
    Gia DECIMAL(10,2),
    SoLuongTon INT,
    SoTrang INT,
    FOREIGN KEY (ID_NXB) REFERENCES NXB(ID_NXB)
);

CREATE TABLE AnhSP (
    ID_Anh INT PRIMARY KEY,
    ID_SP INT,
    STT INT,
    Anh LONGBLOB,
    FOREIGN KEY (ID_SP) REFERENCES SanPham(SanPhamID)
);

CREATE TABLE SP_DM (
    SanPhamID INT,
    DanhMucID INT,
    PRIMARY KEY (SanPhamID, DanhMucID),
    FOREIGN KEY (SanPhamID) REFERENCES SanPham(SanPhamID),
    FOREIGN KEY (DanhMucID) REFERENCES DanhMuc(DanhMucID)
);

CREATE TABLE SP_TG (
    SanPhamID INT,
    IDTacGia INT,
    PRIMARY KEY (SanPhamID, IDTacGia),
    FOREIGN KEY (SanPhamID) REFERENCES SanPham(SanPhamID),
    FOREIGN KEY (IDTacGia) REFERENCES TacGia(IDTacGia)
);

CREATE TABLE KhachHang (
    ID_KH INT PRIMARY KEY,
    TenTK VARCHAR(255) NOT NULL,
    TenKH VARCHAR(255) NOT NULL,
    SDT VARCHAR(15),
    NgaySinh DATE,
    MatKhau VARCHAR(255) NOT NULL
);

CREATE TABLE DiaChi_KH (
    ID_DCKH INT PRIMARY KEY,
    ID_KH INT,
    SoDienThoai VARCHAR(15),
    TenNguoiNhan VARCHAR(255),
    SoNhaDuong VARCHAR(255),
    QuanHuyen VARCHAR(255),
    TinhThanhPho VARCHAR(255),
    FOREIGN KEY (ID_KH) REFERENCES KhachHang(ID_KH)
);

CREATE TABLE GioHang (
    ID_SP INT,
    ID_KH INT,
    SoLuong INT,
    PRIMARY KEY (ID_SP, ID_KH),
    FOREIGN KEY (ID_KH) REFERENCES KhachHang(ID_KH),
    FOREIGN KEY (ID_SP) REFERENCES SanPham(SanPhamID)
);

CREATE TABLE GiamGia (
    IDGiamGia INT PRIMARY KEY,
    TenGiamGia VARCHAR(255) NOT NULL,
    GiaTri DECIMAL(10,2),
    MoTa TEXT,
    NgayBD DATE,
    NgayKT DATE
);

CREATE TABLE NhanVien (
    IDNhanVien INT PRIMARY KEY,
    TenNhanVien VARCHAR(255) NOT NULL,
    NgaySinh DATE,
    Mail VARCHAR(255),
    SDT VARCHAR(15),
    ViTri VARCHAR(255),
    SoNhaDuong VARCHAR(255),
    QuanHuyen VARCHAR(255),
    TinhThanhPho VARCHAR(255),
    NgayVaoLam DATE,
    Luong DECIMAL(10,2)
);

CREATE TABLE HoaDonXuat (
    IDHoaDonXuat INT PRIMARY KEY,
    ID_KH INT,
    IDNhanVien INT,
    ID_GiamGia INT,
    PhuongThucThanhToan ENUM('Tiền mặt', 'Chuyển khoản', 'Credit card'),
    NgayXuat TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    Thue DECIMAL(10,2),
    TongTien DECIMAL(10,2),
    TinhTrangThanhToan ENUM('Đã thanh toán', 'Chưa thanh toán', 'Đã hoàn tiền', 'Chưa hoàn tiền'),

    FOREIGN KEY (ID_KH) REFERENCES KhachHang(ID_KH),
    FOREIGN KEY (IDNhanVien) REFERENCES NhanVien(IDNhanVien),
    FOREIGN KEY (ID_GiamGia) REFERENCES GiamGia(IDGiamGia)
);

CREATE TABLE GiaoHang (
    IDGiaoHang INT PRIMARY KEY,
    ID_HDX INT,
    IDNhanVien INT,
    IDDiaChi INT,
    NgayGiaoHang DATE,
    TinhTrangDon ENUM('Chờ xác nhận', 'Chờ lấy hàng', 'Đang giao hàng', 'Đã giao', 'Trả hàng', 'Đã hủy'),
    FOREIGN KEY (IDNhanVien) REFERENCES NhanVien(IDNhanVien),
    FOREIGN KEY (IDDiaChi) REFERENCES DiaChi_KH(ID_DCKH),
    FOREIGN KEY (ID_HDX) REFERENCES HoaDonXuat(IDHoaDonXuat)
);

CREATE TABLE ChiTietHoaDonXuat (
    IDHoaDonXuat INT,
    IDSanPham INT,
    SoLuong INT,
    ThanhTien DECIMAL(10,2),
    PRIMARY KEY (IDHoaDonXuat, IDSanPham),
    FOREIGN KEY (IDHoaDonXuat) REFERENCES HoaDonXuat(IDHoaDonXuat),
    FOREIGN KEY (IDSanPham) REFERENCES SanPham(SanPhamID)
);

CREATE TABLE HoaDonNhap (
    IDHoaDonNhap INT PRIMARY KEY,
    ID_NCC INT,
    IDNhanVien INT,
    NgayNhap TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    Tongien DECIMAL(10,2),
    FOREIGN KEY (ID_NCC) REFERENCES NCC(ID_NCC),
    FOREIGN KEY (IDNhanVien) REFERENCES NhanVien(IDNhanVien)
);

CREATE TABLE ChiTietHoaDonNhap (
    IDHoaDonNhap INT,
    IDSanPham INT,
    SoLuong INT,
    GiaNhap DECIMAL(10,2),
    PRIMARY KEY (IDHoaDonNhap, IDSanPham),
    FOREIGN KEY (IDHoaDonNhap) REFERENCES HoaDonNhap(IDHoaDonNhap),
    FOREIGN KEY (IDSanPham) REFERENCES SanPham(SanPhamID)
);

CREATE TABLE NhomQuyen (
    ID_NhomQuyen INT PRIMARY KEY,
    TenNhomQuyen VARCHAR(255) NOT NULL
);

CREATE TABLE DanhMucChucNang (
    ChucNang VARCHAR(255) PRIMARY KEY,
    TenQuyen VARCHAR(255)  -- Corrected from TenChucNang to match INSERT statement
);

CREATE TABLE ChiTietQuyen (
    ID_NhomQuyen INT,
    ChucNang VARCHAR(255),
    HanhDong VARCHAR(255),
    PRIMARY KEY (ID_NhomQuyen, ChucNang, HanhDong),
    FOREIGN KEY (ID_NhomQuyen) REFERENCES NhomQuyen(ID_NhomQuyen),
    FOREIGN KEY (ChucNang) REFERENCES DanhMucChucNang(ChucNang)
);

CREATE TABLE TaiKhoan (
    ID_TK INT PRIMARY KEY,
    ID_NhanVien INT,
    ID_NhomQuyen INT,
    MatKhau VARCHAR(255) NOT NULL,
    FOREIGN KEY (ID_NhanVien) REFERENCES NhanVien(IDNhanVien),
    FOREIGN KEY (ID_NhomQuyen) REFERENCES NhomQuyen(ID_NhomQuyen)
);

